() => {
  const title = text('Title', 'Short and clear.', GENERAL_GROUP);
  const isClosable = boolean('Is closable?', false, GENERAL_GROUP);
  const type = select('Type', types, 'neutral', GENERAL_GROUP);

  const actionLabel = text('Action label', 'Message action', ACTIONS_GROUP);
  const isActionLoading = boolean('Is action loading?', false, ACTIONS_GROUP);
  const isActionDisabled = boolean('Is action disabled?', false, ACTIONS_GROUP);

  const bannerAction = {
    label: actionLabel,
    onClick: action('Action clicked'),
    isDisabled: isActionDisabled,
    isLoading: isActionLoading,
  };

  return (
    <Container width={520}>
      <CompactAlertBanner
        action={bannerAction}
        title={title}
        type={type}
        onClose={isClosable ? action('AlertBanner closed') : undefined}
        dataCy="CompactAlertBanner"
      />
    </Container>
  );
}

() => {
  const title = text('Title', 'Short and clear.', GENERAL_GROUP);
  const message = text('Message', 'This is how it is done.', GENERAL_GROUP);
  const isClosable = boolean('Is closable?', false, GENERAL_GROUP);
  const isFloating = boolean('Is floating?', false, GENERAL_GROUP);
  const customIcon = boolean('Use custom icon?', false, GENERAL_GROUP);
  const icon = customIcon
    ? select('Icon', iconNames, iconNames[0], GENERAL_GROUP)
    : undefined;
  const type = select('Type', types, 'neutral', GENERAL_GROUP);

  const hasAction = boolean('Has action?', false, ACTIONS_GROUP);
  const hasActionIcon = hasAction
    ? boolean('Has action icon?', false, ACTIONS_GROUP)
    : undefined;
  const actionIcon = hasActionIcon
    ? select('Action icon', iconNames, iconNames[0], ACTIONS_GROUP)
    : undefined;
  const actionLabel = hasAction
    ? text('Action label', 'Message action', ACTIONS_GROUP)
    : '';
  const isActionLoading = boolean('Is action loading?', false, ACTIONS_GROUP);
  const isActionDisabled = boolean('Is action disabled?', false, ACTIONS_GROUP);

  const bannerAction = useMemo(() => {
    const ActionIcon = actionIcon ? Icons[actionIcon] : null;

    return {
      icon: ActionIcon ? <ActionIcon /> : undefined,
      label: actionLabel,
      onClick: action('Action clicked'),
      isDisabled: isActionDisabled,
      isLoading: isActionLoading,
    };
  }, [actionIcon, actionLabel, isActionDisabled, isActionLoading]);

  const IconComponent = icon ? Icons[icon] : null;

  return (
    <Container width={520}>
      <CompactAlertBanner
        action={hasAction ? bannerAction : undefined}
        isFloating={isFloating}
        icon={IconComponent ? <IconComponent /> : undefined}
        onClose={isClosable ? action('AlertBanner closed') : undefined}
        title={title}
        type={type}
        dataCy="CompactAlertBanner"
      >
        {message}
      </CompactAlertBanner>
    </Container>
  );
}

() => {
  const appeared = boolean('Toggle appear', true);
  const entered = boolean('Toggle enter', true);
  const animateOnAppear = boolean('Animate on appear', true);
  const animateOnEnter = boolean('Animate on enter', false);
  const animateOnExit = boolean('Animate on exit', false);

  if (!appeared) {
    return null;
  }

  return (
    <AnimatedComponent
      animateOnAppear={animateOnAppear}
      animateOnEnter={animateOnEnter}
      animateOnExit={animateOnExit}
      transition="fade"
    >
      {entered && (
        <>
          <div>Component to render</div>
          <div>2nd component to render</div>
        </>
      )}
    </AnimatedComponent>
  );
}

() => {
  const hasError = boolean('Has error', false);
  const isDisabled = boolean('Is disabled', false);
  const numberOfRows = number('Number of rows', 1);

  return (
    <AutosizeTextarea
      type="text"
      placeholder="Textarea"
      maxLength={255}
      rows={numberOfRows}
      disabled={isDisabled}
      error={hasError}
    />
  );
}
